import styles from './Slider.modules.css'
import photosGymList from './photosGymList'
import { useState } from 'react'

const Slider = () => {
  const [position, setNewPosition] = useState(-860)

  const arrowLeftHandle = () => {
    setNewPosition((curr) => {
      const currNew = curr + 860
      return Math.min(currNew, 0)
    })
  }

  const arrowRightHandle = () => {
    setNewPosition((curr) => {
      const maxCurr = (photosGymList.length - 1) * 860
      
      const currNew = curr - 860
      return currNew
    })
  }

  return (
    <div className={styles}>
      <div className="slider">
        <div className="buttonLeft" onClick={arrowLeftHandle} />

        <div
          className="sliderImg"
          style={{
            transform: `translateX(${position}px)`,
          }}
        >
          {photosGymList.map((img, index) => {
            return (
              <div key={index}>
                <img src={img} alt={'photo of GYM ' + index} />
              </div>
            )
          })}
        </div>
        <div className="buttonRight" onClick={arrowRightHandle} />
      </div>
    </div>
  )
}

export default Slider
