import styles from './Slider.module.css'
import photosGymList from './photosGymList'
import { useEffect, useState } from 'react'

const IMG_WIDTH = 860
const TRANSITION_DURATION = 500

const [] = [
  photosGymList[photosGymList.length - 1],
  ...photosGymList,
  photosGymList[0],
]

const Slider = ({ infinite }) => {
  const [position, setNewPosition] = useState(-IMG_WIDTH)
  const [images, setImages] = useState(listExtend)
  const [imgHeadTail, setImgHeadTail] = useState({ head: 0, tail: 0 })
  const [transDuration, setTransDuration] = useState(TRANSITION_DURATION)

  const arrowLeftHandle = () => {
    setNewPosition((curr) => {
      const currNew = curr + IMG_WIDTH
      return Math.min(currNew, 0)
    })
  }

  const arrowRightHandle = () => {
    setNewPosition((curr) => {
      const maxCurr = -(IMG_WIDTH * (images.length - 1))
      const currNew = curr - IMG_WIDTH
      return Math.max(currNew, maxCurr)
    })
  }

  useEffect(() => {
    if (infinite) {
      setImages(images)
      setImgHeadTail({ head: 1, tail: 1 })
      return
    }
    setImages(photosGymList)
  }, [infinite])

  useEffect(() => {
    if (!infinite) return

    if (position === 0) {
      setTimeout(() => {
        setTransDuration(0)
        setNewPosition(-(IMG_WIDTH * (listExtend.length - 1 - 1)))
      }, TRANSITION_DURATION)
      return
    }
    if (position === -(IMG_WIDTH * (listExtend.length - 1))) {
      setTimeout(() => {
        setTransDuration(0)
        setNewPosition(-IMG_WIDTH)
      }, TRANSITION_DURATION)
      return
    }
  }, [infinite, position])

  useEffect(() => {
    if (transDuration === 0) {
      setTimeout(() => {
        setTransDuration(TRANSITION_DURATION)
      }, TRANSITION_DURATION)
    }
  }, [transDuration])

  return (
    <div className={styles.slider}>
      <div className={styles.buttonLeft} onClick={arrowLeftHandle} />

      <div
        className={styles.sliderImg}
        style={{
          transitionDuration: `${transDuration}ms`,
          transform: `translateX(${position}px)`,
        }}
      >
        {listExtend.map((img, index) => {
          return (
            <div key={index}>
              <img src={img} alt={'photo of GYM ' + index} />
            </div>
          )
        })}
      </div>

      <div className={styles.buttonRight} onClick={arrowRightHandle} />
    </div>
  )
}

export default Slider
