import { useState } from 'react'
import style from './EmailForm.module.css'

const EmailForm = () => {
  const [clientEmail, setClientEmail] = useState('')

  const handlerEmailClient = (e)

  return (
    <div className={style.emailForm}>
      <div className={style.leftDarkSide} />
      <form className={style.formInput}>
        <label>
          <span className={style.frameText}>Оставайся на связи:</span>{' '}
          <input
            className={style.emailInput}
            value={clientEmail}
            onChange={(e) => setClientEmail(e.target.value)}
            onSubmit={handlerEmailClient}
            placeholder="email"
          />
        </label>
        <button type="submit" className={style.formSubmit}>
          Отправить
        </button>
      </form>
      <div className={style.rightDarkSide} />
    </div>
  )
}

export default EmailForm
