import styles from './Slider.modules.css'
import photosGymList from './photosGymList'
import Button from './Button'
import { useState } from 'react'

const Slider = () => {
  const [position, setRightStep] = useState(-1560)

  const arrowLeftHandle = () => {
    setRightStep(position - 860)
  }

  const arrowRightHandle = (position) => {
    setRightStep(position - 860)
  }

  return (
    <div className={styles}>
      <div className="slider">
        <div
          className="sliderImg"
          style={{
            transform: `translateX(${position}px)`,
          }}
        >
          {photosGymList.map((img, index) => {
            return (
              <div key={index}>
                <img src={img} alt={'photo of GYM ' + index} />
              </div>
            )
          })}
        </div>
        <div className="buttons">
          <Button onClick={arrowLeftHandle} />
          <Button onClick={arrowRightHandle} />
        </div>
      </div>
    </div>
  )
}

export default Slider
