import { useState } from 'react'
import styles from './App.module.css'
import MainPage from './components/MainPage/Page'
import FuncButtons from './components/UI/FuncBtn/FuncButtons'

function App() {
  const [dayNightMode, setDayNightMode] = useState(styles.AppNightMode)

  let style = dayNightMode

  const handleDayNightMode = (currMode) => {
    setDayNightMode(currMode)
  }

  return (
    <div className={dayNightMode}>
      <MainPage />
      <FuncButtons switchMod={handleDayNightMode} currStyle={style}/>
    </div>
  )
}

export default App
