import style from './Features.module.css'
import photoSecond from '../photos/photoSecond.png'
import photoFeartMan from '../photos/photoFeartMan.png'
import photoCrossFit from '../photos/photoCrossFit.png'
import photoGroup from '../photos/photoGroup.png'
import LineRedHorr from '../../UI/LineRedHorr'
import { useEffect, useState } from 'react'

const Features = () => {
  const [position, setNewPosition] = useState(150)

  // window.addEventListener('scroll', function () {
  //   let posYscroll = window.scrollY
  //   return posYscroll
  // })

  // window.onscroll = function () {
  //   let scrolled = window.scrollY
  //   console.log('Позиция скрола: ' + scrolled)
  // }



  document
    .getElementsByClassName('frameAction')
    .addEventListener('scroll', function () {
      return console.log('Позиция скрола у элемента: ' + this.scrollTop)
    })

  return (
    <div className="frameAction">
      <LineRedHorr />
      <div className={style.featuresContainer}>
        <div
          className={style.frameFeatures}
          style={{
            transform: `translateY(${position}px)`,
          }}
        >
          <img src={photoSecond} alt={'photo of Features'}></img>
          <div className={style.frameText}>Доступ 24 часа во все залы</div>
        </div>
        <div className={style.frameFeatures}>
          <img src={photoFeartMan} alt={'photo of Features'}></img>
          <div className={style.frameText}>
            Большой штат тренеров и специалистов
          </div>
        </div>
        <div className={style.frameFeatures}>
          <img src={photoCrossFit} alt={'photo of Features'}></img>
          <div className={style.frameText}>
            Функциональные залы под любые нужды
          </div>
        </div>
        <div className={style.frameFeatures}>
          <img src={photoGroup} alt={'photo of Features'} />
          <div className={style.frameText}>
            Групповые занятия и кроссфит зоны
          </div>
        </div>
      </div>
      <LineRedHorr />
    </div>
  )
}

export default Features
