import styles from './Slider.modules.css'
import photosGymList from './photosGymList'
import Button from './Button'
import { useState } from 'react'

const Slider = () => {
  const [positionR, setRightStep] = useState(-700)
  const [positionL, setLeftStep] = useState(-700)

  const arrowLeftHandle = () => {
    setLeftStep(-1560)
  }

  const arrowRightHandle = () => {
    setRightStep(-2420)
  }

  return (
    <div className={styles}>
      <div className="slider">
        <Button onClick={arrowLeftHandle} className='butto' />

        <div
          className="sliderImg"
          style={{
            transform: `translateX(${positionL}px)`,
          }}
        >
          {photosGymList.map((img, index) => {
            return (
              <div key={index}>
                <img src={img} alt={'photo of GYM ' + index} />
              </div>
            )
          })}
        </div>

        <Button onClick={arrowRightHandle} />
      </div>
    </div>
  )
}

export default Slider
