import styles from './FuncButtons.module.css'
import DayNightMode from './DayNightMod.jsx'
import ScrollTop from './ScrollTop.jsx'
import { useState } from 'react'

const FuncButtons = ({ onClick }) => {
  const [listenerScroll, setListenerScroll] = useState('')

  onClick("styles.AppDayMode")

  const handlerScroll = () => {
    setListenerScroll(window.scrollY)
  }

  window.addEventListener('scroll', handlerScroll)

  return (
    <div className={styles.funcBtns}>
      <> <DayNightMode /> </>
      {!!listenerScroll && <ScrollTop />}
    </div>
  )
}

export default FuncButtons
